mapper.select:
SELECT ID , NAME , AGE , NOTICE , DATE_FORMAT(CRT_DATE, '%Y-%m-%d %H:%i:%s') AS CRT_DATE , CRT_ID FROM TB_SAMPLE
AND ID = #{id}
AND NAME LIKE CONCAT(#{name},'%')
AND AGE = #{age}

mapper.insert:
INSERT INTO TB_SAMPLE (
    ID
    , NAME
    , AGE
    , NOTICE
    , CRT_DATE
    , CRT_ID
) VALUES (
    #{id}
    , #{name}
    , #{age}
    , #{notice}
    , NOW()
    , #{crt_id}
)

mapper.update:
UPDATE TB_SAMPLE
    NAME = #{name}
    , AGE = #{age}
    , NOTICE = #{notice}
    , CRT_DATE = NOW()
WHERE ID = #{id}
